select count(distinct(r.customer_id)) as no_of_customers from rental r join customer c on r.customer_id=c.customer_id join inventory i on r.inventory_id=i.inventory_id join film_category fc on i.film_id=fc.film_id join category ca on fc.category_id=ca.category_id where ca.name="Horror" and r.customer_id not in (select r.customer_id from rental r join customer c on r.customer_id=c.customer_id join inventory i on r.inventory_id=i.inventory_id join film_category fc on i.film_id=fc.film_id join category ca on fc.category_id=ca.category_id where ca.name="Comedy");